import React from 'react';
import './App.css';
import Person from './Person/Person.js';
import Manish  from "./Person/Button.js";
import Number from "./Person/Spread.js";
import Container from "./Person/container.js";
import Slider from './Person/conditions.js';
import ReactDOM from 'react-dom';



class Car extends React.Component {
 state = {
  nameofcontainer : [ 
    {name:"manish"},
    {name:"vardhan"},
    {name:"cruse"}

  ]
 }

 switchHandler = () => {
  // console.log('was clicked'); 
  this.setState( {
    nameofcontainer : [ 
      {name: "Manish"},
      {name:"vardhan"},
      {name:"cruse vavilala"}

    ]
  } )
 }

  render() {
    return (
      <div className = 'App'>
        {/* this is header */}
        <Person  />
        {/* this is header */}

      <button onClick = {this.switchHandler}>switch containers</button>

        {/* this is block */}     
      <Container name= {this.state.nameofcontainer[0].name} click = {this.switchHandler("man")} >this is slider part</Container>
      <Container name= {this.state.nameofcontainer[1].name} click = {this.switchHandler("varu")}>this is slider part</Container>
      <Container name = {this.state.nameofcontainer[2].name}>this is slider part</Container>
        {/* this is block */}


        {/* this is footer */} 
      <Manish />
        {/* this is footer */}
      </div>
    );
  }
}

export default Car;
