import React from 'react';
import './App.css';
import Person from './Person/Person.js';
import Manish  from "./Person/Button.js";
import Number from "./Person/Spread.js";
import Container from "./Person/container.js";
import Slider from './Person/conditions.js';
import ReactDOM from 'react-dom';



class Car extends React.Component {
 state = {
  nameofcontainer : [ 
    {name:"manish"},
    {name:"vardhan"},
    {name:"cruse"}

  ],
  showState: false
 }

 switchHandler = (newName) => {
  // console.log('was clicked'); 
  this.setState( {
    nameofcontainer : [ 
      {name: newName },
      {name:"vardhan"},
      {name:"cruse vavilala"}


    ]
  } )
 }
 nameChangeHandler1 = (event) => {
  this.setState( {
    nameofcontainer : [ 
      {name:event.target.value},
      {name: "cruse"},
      {name:"sai"}

    ]
  } )
 }
 nameChangeHandler2 = (event) => {
  this.setState( {
    nameofcontainer : [ 
      {name:"sai"},
      {name: event.target.value},
      {name:"vavilala"}

    ]
  } )
 }
 nameChangeHandler3 = (event) => {
  this.setState( {
    nameofcontainer : [ 
      {name:"sai"},
      {name: "vavilala"},
      {name:event.target.value}

    ]
  } )
 }
 toggleHandler= () => {
    const show = this.setState.showState;
    this.setState({showState : !show});
 }

  render() {
    const style = {
      backgroundColor : "white",
      font : "inherit",
      border : "1px solid blue",
      padding : "8px",
      cursor : "pointer",
      textAlign : "center",
    };
    let slider = null;
    if(this.state.showState ) {
      slider = (
        <div>
        <Container 
        name= {this.state.nameofcontainer[0].name}
        click = {this.switchHandler}
        changed= {this.nameChangeHandler1} >this is slider part
        </Container>

        <Container 
        name= {this.state.nameofcontainer[1].name} 
        click = {this.switchHandler}
        changed= {this.nameChangeHandler2}>this is slider part
        </Container>

        <Container 
          name = {this.state.nameofcontainer[2].name}
        changed= {this.nameChangeHandler3}>this is slider part
        </Container> 
        </div> 
      );
      console.log("sucess");

    } else {
      console.log('vardhan');

    }

    return (
      <div className = 'App'>
        {/* this is header */}
        <Person  />
        {/* this is header */}

      <button style={style} onClick = {this.toggleHandler ? {slider} : null}>toggle  containers</button>

          {slider}
              {/* this is block */}     

            <Manish > </Manish>











      </div>
    );

  }
}

export default Car;
